  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'uncontrolled-input',
	title: 'React form with Uncontrolled Input',
	description: 'Uncontrolled forms are the most performant way to create react forms. With uncontrolled input values, there is no updating or changing of any states. What you submit is what you get.'
};
export const metadata = {
  "unversionedId": "old/uncontrolled-input",
  "id": "old/uncontrolled-input",
  "isDocsHomePage": false,
  "title": "React form with Uncontrolled Input",
  "description": "Uncontrolled forms are the most performant way to create react forms. With uncontrolled input values, there is no updating or changing of any states. What you submit is what you get.",
  "source": "@site/docs/old/uncontrolled-input.mdx",
  "slug": "/old/uncontrolled-input",
  "permalink": "/docs/old/uncontrolled-input",
  "editUrl": "https://github.com/Jucian0/useform/tree/docs/docs/old/uncontrolled-input.mdx",
  "version": "current"
};
  /* @jsxRuntime classic */
/* @jsx mdx */
import Uncontrolled from './../src/components/uncontrolled'
export const rightToc = [{
  value: 'Description',
  id: 'description',
  children: []
}, {
  value: 'Instalation',
  id: 'instalation',
  children: []
}, {
  value: 'Usage',
  id: 'usage',
  children: []
}, {
  value: 'Demo',
  id: 'demo',
  children: []
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <h2 {...{
      "id": "description"
    }}>{`Description`}</h2>
    <p>{`There are two types of form input in React. We have the uncontrolled input and the controlled input. Uncontrolled inputs are like traditional HTML form inputs:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-html"
      }}>{`   <div>
      <input type="text" />
   </div>
`}</code></pre>
    <p>{`With uncontrolled input values, there is no updating or changing of any states. When we use uncontrolled input is necessary to set a reference in input for manipulating and receives events, but with userForm it isn't necessary because userForm makes this for us.
Uncontrolled inputs are useful when performance is essential. But if the forms turn very complex debounce inputs are the best option.`}</p>
    <p>{`Ps * You do not can use uncontrolled form with custom inputs because custom inputs use state in your internal logic.
If you want to use uncontrolled form with custom inputs and native inputs the uncontrolled approach is applied only native inputs.`}</p>
    <p>{`React Docs`}</p>
    <p>{`By default, `}<inlineCode parentName="p">{`useForm`}</inlineCode>{` use uncontrolled inputs, isn't necessary to pass any value to useForm hook if you want to use this kind.
But when you use this kind of form, it is necessary to realize that values and events validation are available only when onSubmit event is dispatched.`}</p>
    <h2 {...{
      "id": "instalation"
    }}>{`Instalation`}</h2>
    <pre><code parentName="pre" {...{}}>{`  npm install useforms
`}</code></pre>
    <pre><code parentName="pre" {...{}}>{`  yarn add useforms
`}</code></pre>
    <h2 {...{
      "id": "usage"
    }}>{`Usage`}</h2>
    <p><inlineCode parentName="p">{`useForm`}</inlineCode>{` provides a `}<inlineCode parentName="p">{`input`}</inlineCode>{` function, this function as a link with input and a object property of form state.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`   import {useForm} from 'useforms'

/*
 *  initial Values optional
*/
   const initialValues = {
      data1:"test",
      complextData:[
         {
         first:"testComplexData",
         score:45
         }
      ]
   }

   const [{values, reset, onSubmit},{input}] = useForm({})
`}</code></pre>
    <p>{`Use dot notation to create advanced objects or to map object values. Type an entry name and type or an entry property object.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`   <form onSubmit={onSubmit(e=> console.log(e))} onReset={reset}>
      <input {...input("data1", "text")}/>
      <input {...input("complexData.0.first", "text")}/>
      <input {...input({name:"complexData.0.score", type:"number"})}/>
      <button type="submit">Submit</button>
      <button type="reset">Reset</button>
   </form>
`}</code></pre>
    <h2 {...{
      "id": "demo"
    }}>{`Demo`}</h2>
    <Uncontrolled mdxType="Uncontrolled" />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
